mapper.select:
SELECT
    fb.bill_id,
    fb.amount,
    fb.user_name,
    fb.type,
    fb.flow,
    fb.pay_time,
    fb.remark,
    fa.name as account_name
FROM
    family_bill as fb JOIN family_account fa
WHERE fb.del_flag = 0 AND fb.account_id = fa.account_id

    AND fb.user_name = #{query.userName}


    AND fb.type = #{query.type}


    AND fb.account_id = #{query.accountId}


    AND fb.flow = #{query.flow}


    and fb.pay_time >= #{query.params.beginTime}


    and fb.pay_time <= concat(#{query.params.endTime},' 23:59:59')


${query.params.dataScope}

SELECT
    fb.flow,
    sum( fb.amount ) as amount
FROM
    family_bill fb
WHERE fb.del_flag = 0


            AND fb.user_name = #{query.userName}


            AND fb.account_id = #{query.accountId}


            AND fb.type = #{query.type}


            AND fb.flow = #{query.flow}


            and fb.pay_time >= #{query.params.beginTime}


            and fb.pay_time <= concat(#{query.params.endTime},' 23:59:59')



${query.params.dataScope}
GROUP BY
    fb.flow

SELECT CASE WHEN user_name IS NULL OR user_name = '' THEN '夏周之家' ELSE user_name END AS user_name, SUM( amount ) AS amount FROM family_bill fb WHERE del_flag = 0 AND flow = 2
and pay_time >= #{query.params.beginTime}
and pay_time <= concat(#{query.params.endTime},' 23:59:59')
${query.params.dataScope} GROUP BY CASE WHEN user_name IS NULL OR user_name = '' THEN '夏周之家' ELSE user_name END;
